  `img_list` varchar(1000) DEFAULT NULL COMMENT '图片url集合',
  `recommend` tinyint(1) DEFAULT '0' COMMENT '是否推荐(0:不推荐,1:推荐)',
  `work_time` varchar(50) DEFAULT NULL COMMENT '营业时间',
  `alipay_account` varchar(50) DEFAULT NULL COMMENT '支付宝账号',
  `alipay_autoym` varchar(50) DEFAULT NULL COMMENT '门店支付宝实名',
  `crm` varchar(50) DEFAULT NULL COMMENT '门店编号',
  `asm` varchar(50) DEFAULT NULL COMMENT '保留',
  `sr` varchar(50) DEFAULT NULL COMMENT '保留',
  `bd_lon_lat` varchar(100) DEFAULT NULL COMMENT '百度经纬度',
  `tx_lon_lat` varchar(100) DEFAULT NULL COMMENT '腾讯经纬度',
  `gd_lon_lat` varchar(100) DEFAULT NULL COMMENT '高德经纬度(默认以高德为主)',
